import { useState } from "react";
import Modal from "../UI/Modal";
import classes from "./Checkout.module.css";
import useInput from "../../hooks/use-input";
// import classes from './Modal.module.css';

const Checkout = (props) => {
  const {
    value: enteredName,
    inputHasError: nameInputIsInvalid,
    valueInputHandler: nameInputHandler,
    valueInputBlurHandler: nameInputOnblurHandler,
    reset: resetValue,
  } = useInput(value => value.trim() !== '');

  const [enteredStreet, setEnteredStreet] = useState("");
  const [enteredStreetTouch, setEnteredStreetTouch] = useState(false);


  //   const enteredInputIsInvalid = enteredName.trim() !== "" && enteredStreet.trim() !== ""
  //   && enteredPostal.trim() !== '' && enteredCity.trim() !== '';
  const enteredNameIsValid = enteredName.trim() !== "";
//   const NameInputIsInvalid = !enteredNameIsValid && enteredNameTouch;

  const enteredStreetIsValid = enteredStreet.trim() !== "";
  const StreetInputIsInvalid = !enteredStreetIsValid && enteredStreetTouch;

  let formIsInvalid = true;
  if (enteredNameIsValid && enteredStreetIsValid) {
    formIsInvalid = false;
  }

  const confirmHandler = (event) => {
    event.preventDefault();
    if (!nameInputIsInvalid) {
      return;
    }
    resetValue();
    setEnteredStreet("");
  };

//   const nameInputHandler = (event) => {
//     setEnteredName(event.target.value);
//   };

//   const nameInputOnblurHandler = (event) => {
//     setEnteredNameTouch(true);
//   };

  const streetInputHanler = (event) => {
    setEnteredStreet(event.target.value);
  };

  const streetInputOnblurHandler = (event) => {
    setEnteredStreetTouch(true);
  };



  return (
    <form onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputHandler}
          onBlur={nameInputOnblurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className="errortext">Name must not empty.</p>
        )}
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          onChange={streetInputHanler}
          onBlur={streetInputOnblurHandler}
        />
        {StreetInputIsInvalid && (
          <p className="errortext">Street must not empty.</p>
        )}
      </div>
      <div className={classes.control}>
        <label htmlFor="postalCode">Postal Code</label>
        <input type="text" id="postalCode"  />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city"  />
      </div>
      {/* {!enteredInputIsInvalid && <p className="error-text">Input are required.</p>} */}
      <button type="button" onClick={props.onCancel}>
        Cancel
      </button>
      <button disabled={!formIsInvalid}>Confirm</button>
    </form>
  );
};

export default Checkout;
