import { useState } from "react";
import Modal from "../UI/Modal";
import classes from "./Checkout.module.css";
// import classes from './Modal.module.css';

const Checkout = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredStreet, setEnteredStreet] = useState("");
  const [enteredPostal, setEnteredPostal] = useState("");
  const [enteredCity, setEnteredCity] = useState("");

  const enteredInput = enteredName.trim() !== "" && enteredStreet.trim() !== "" 
  && enteredPostal.trim() !== '' && enteredCity.trim() !== '';
  const confirmHandler = (event) => {
    event.preventDefault();
  };

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const streetInputHanler = (event) => {
    setEnteredStreet(event.target.value);
  };

  const postalInputHanler = (event) => {
    setEnteredPostal(event.target.value);
  };

  const cityInputHanler = (event) => {
    setEnteredCity(event.target.value);
  };

  return (
    <form onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" onChange={nameInputHandler} />
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" onChange={streetInputHanler}/>
      </div>
      <div className={classes.control}>
        <label htmlFor="postalCode">Postal Code</label>
        <input type="text" id="postalCode" onChange={postalInputHanler}/>
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" onChange={cityInputHanler}/>
      </div>
      <button type="button" onClick={props.onCancel}>
        Cancel
      </button>
      <button>Confirm</button>
    </form>
  );
};

export default Checkout;
